

public class OperatorEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 5;
		int n = 0;
		n = i++;
		System.out.println("n = i++; 수행후 -> i= " + i + ", n= " + n);
		
		
		i = 5;
		n = 0;
		n = ++i;
		System.out.println("n = ++i; 수행후 -> i= " + i + ", n= " + n);
		
	}

}
